import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useHistory } from 'react-router-dom';

import './app-header.css';

const AppHeader = () => {
    const autor = useSelector(state => state.autor)


    let history = useHistory();

    const handleClick = () => {
        history.push("/list");
    }

    const logIn = () => {
        history.push("/login")
    }

    let ren = autor ? <button>TRUE</button> : <button className="log btn btn-dark" onClick={logIn}>Sing in</button>;


    return (
        <header className="header">
            <Link to={'/'} className="h3 text-light">PIPITER</Link>
            <nav className="navbar navbar-light">
                <form className="form-inline" onSubmit={(e) => e.preventDefault()}>
                    <input className="form-control mr-sm-2 " type="search" placeholder="Search" aria-label="Search" />
                    <button className="btn btn-outline-secondary my-2 my-sm-0" type="submit" onClick={handleClick}>Search</button>
                </form>
            </nav>
            {/*<button className="log btn btn-dark" onClick={logIn}>Sing in</button>*/}
            {ren}
        </header>
    )
}



export default AppHeader;
