const createNewPost = (postForm, setError, history, id, image) => {
    return (dispatch) =>
        api.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/posts",
            { ...postForm },
        )
            .then(res => {
                if (res.status === 200) {
                    if (image) {
                        let formData = new FormData()
                        formData.append('image', image)
                        dispatch(updatePostImage(res.data._id, formData))
                    }
                    setTimeout(() => history.push(`/user-posts/${id}`), 1500)
                }
            })
            .catch(e => {
                console.log(e.response)
                setError(e.response.data.error[0].message)
            })
}

const updatePostImage = (id, image, refImagePost) => {
    return (dispatch) =>
        api.put(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/upload/${id}`, image, {
            headers: {
                "Content-Type": ' multipart/form-data'
            }
        })
            .then(res => {
                console.log(res)
                dispatch(getImage(refImagePost, res.data.image))
            })


}

const getImage = (ref, image, defaultImage, alt) => {
    return () =>
        axios.get(`https://nodejs-test-api-blog.herokuapp.com${image}`)
            .then(res => {
                if (res.statusText === 'OK') {
                    ref.current.src = `https://nodejs-test-api-blog.herokuapp.com${image}`
                }
            })
            .catch(e => {
                if (e.status >= 400) {
                    ref.current.src = defaultImage;
                    ref.current.alt = alt;
                }
            })
}

const updatePost = (id, title, description, fullText, history, user, setError) => {
    return () =>
        api({
            method: 'PATCH',
            url: `https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`,
            data:
            {
                title,
                description,
                fullText,
            },
        })
            .then(res => setTimeout(() => history.push(`/user-posts/${user._id}`), 1500))
            .catch(e => setError(e.response.data.error[0].message))
}

export { getPosts, setPosts, loaded, fault, deletePost, getPost, getAllPosts, getFullPost, setLikePost, createNewPost, updatePostImage, updatePost }


