import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
import { useHistory } from 'react-router-dom';
import { posts, setPost, setPostUser, getAllUsers } from '../../actions';
import Spinner from '../spinner';

import prof from './no-avatar.jpg'
//import deleteUser from './delete-user.jpg'
import './post-list-item.css';
import axios from 'axios';


const PostListItem = ({ Server }) => {

    const allPosts = useSelector(state => state.allPosts)
    const allUsers = useSelector(state => state.allUsers)
    const loading = useSelector(state => state.loading)
    const dispatch = useDispatch()

    let history = useHistory();
    //let user = JSON.parse(localStorage.getItem('user'));

    //const getUsers = () => {
    //    Server.getAllUsers()
    //        .then(res => console.log(res.data))
    //}

    useEffect(() => {
        Server.getAllUsers()
            .then(res => {
                dispatch(getAllUsers(res.data))
            })
            .catch(er => console.log(er))
    }, [Server, dispatch])

    useEffect(() => {
        Server.getAllPosts()
            .then(res => {
                dispatch(posts(res.data))
            })
            .catch(er => console.log(er))
    }, [Server, dispatch])

    const goToUserPost = async (idPost, idUser) => {
        await Server.getPost(idPost)
            .then(res => dispatch(setPost(res.data)))
            .catch(res => dispatch(setPost(null)))
        await Server.getUser(idUser)
            .then(res => dispatch(setPostUser(res.data)))
            .catch(res => dispatch(setPostUser(null)))
        history.push('/post')
    }

    let idx = 0;

    // eslint-disable-next-line array-callback-return
    const srcAvatar = allUsers.map(usr => {
        axios.get(`https://nodejs-test-api-blog.herokuapp.com${usr.avatar}`)
            .then(res => {
                if (res.statusText === 'OK') {
                    return `https://nodejs-test-api-blog.herokuapp.com${usr.avatar}`
                }
            })
            .catch(e => {
                return 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'
            })
    })

    const itempos = allPosts.map((postItem, i) => {
        idx++

        const user = allUsers.map(usr => {

            if (postItem.postedBy === usr._id) {
                return (
                    <div className="name" key={usr._id}>
                        <img src={prof} className="photo" alt="prof" />
                        <p> {usr.name}</p>
                    </div>
                )
                //return (
                //    <p> {usr.name}</p>
                //)
            }
            return undefined
        })

        //const text =
        //    <div className="name" key={idx + 1}>
        //        <img src={deleteUser} className="photo" alt="prof" />
        //        <p className="delete-user">User deleted</p>
        //    </div>

        return (
            <div className="post" key={idx} onClick={() => console.log(srcAvatar)}>
                <div className="post-list-item" >
                    {user}
                    {/*{user[i] !== undefined ? user : text}*/}
                    {/*<div className="name">
                        <img src={prof} className="photo" alt="prof" />
                        {user}
                    </div>*/}
                    <div className="cards">
                        <p className="title">{postItem.title}</p>
                        <ul className="full-text">{postItem.description}</ul>
                        <div className="full-post">
                            <p className='text'>View full post</p>
                            <button
                                className='delete'
                                onClick={() => goToUserPost(postItem._id, postItem.postedBy)}>
                                <i className="fa fa-arrow-circle-right fa-2x"></i>
                            </button>
                        </div>
                    </div>
                </div>
            </div>

        )
    })


    return (
        <div className="post-list">
            {
                !loading
                    ? itempos
                    : <Spinner />
            }
        </div>
    )
}


export default WithServer()(PostListItem);
