//import axios from 'axios';
import { LOGIN_USER, LOGOUT, LOADED, DELETED, ALL_USERS, POST_USER, AUTH_TOKEN } from './types'


import api from '../../services/api';

const authorizationUser = () => {
    return (dispatch) =>
        api.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth/user')
            .then(res => {
                dispatch({ type: LOGIN_USER, payload: res.data })
                let saveUser = JSON.stringify(res.data)
                localStorage.setItem('user', saveUser)
            })
            .catch(e => console.log(e.response))

}

function loginUser(email, password, setError, nav) {
    return (dispatch) =>
        api.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", {
            email,
            password,
        })
            .then(res => {
                api.defaults.headers['Authorization'] = `Bearer ${res.data.token}`
                dispatch({ type: AUTH_TOKEN, payload: res.data.token })
                if (nav) {
                    nav(`/list/page${1}/?limit=10&skip=${0}`)
                }
                localStorage.setItem('token', res.data.token)
                dispatch(authorizationUser())
            })
            .catch(e => {
                e.response.status === 404
                    && setError({ error: true, message: 'Такого пользователя не существует' })
                if (nav) {
                    nav('/login')
                }
            })
}

const setUser = (user) => {
    return (dispatch) =>
        dispatch({ type: LOGIN_USER, payload: user })
}

const logout = (history) => {
    return (dispatch) => {
        dispatch({ type: LOGOUT })
        dispatch({ type: AUTH_TOKEN, payload: null })
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        history.push("/login");
    }
}

const loaded = () => {
    return (dispatch) =>
        dispatch({ type: LOADED })
}

const deleteUser = (id, token, history) => {
    return (dispatch) => {
        api.delete(`https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`)
            .then(res => {
                dispatch({ type: LOGOUT })
                dispatch({ type: DELETED, payload: true })
                localStorage.removeItem('user');
                localStorage.removeItem('token');
                setTimeout(() => {
                    history.push("/login")
                    dispatch({ type: DELETED, payload: false })
                }, 500)
            })
    }
}

const createNewUser = (email, password, name, history) => {
    return (dispatch) =>
        api.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/users", {
            email,
            password,
            name
        })
            .then(res => history.push('/login'))
            .catch(e => {
                console.log(e.response)
            })

}

const getUserAvatar = (ref, image, defaultImage, alt) => {
    api.get(`https://nodejs-test-api-blog.herokuapp.com${image}`)
        .then(res => {
            if (res.statusText === 'OK') {
                ref.current.src = `https://nodejs-test-api-blog.herokuapp.com${image}`
            }
        })
        .catch(e => {
            if (e.status >= 400) {
                ref.current.src = defaultImage;
                ref.current.alt = alt;
            }
        })
}

const getAllUsers = () => {
    return (dispatch) =>
        api.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/users')
            .then(res => {
                dispatch({ type: ALL_USERS, payload: res.data })
                dispatch({ type: LOADED })
            })


}

const getPostUser = (id, setError) => {
    return (dispatch) =>
        api.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`)
            .then(res => {
                dispatch({ type: POST_USER, payload: res.data })
            })
            .catch(e => setError(true))
}

const updateUserName = (id, name, setCurrentName, setName) => {
    return (dispatch) =>
        api({
            method: 'PATCH',
            url: `https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`,
            data:
            {
                name,
            },
        })
            .then(res => {
                localStorage.setItem('user', JSON.stringify(res.data))
                dispatch(setUser(res.data))
                setCurrentName(name)
                setName('')
            })
}


export { loginUser, setUser, logout, loaded, deleteUser, createNewUser, getUserAvatar, getAllUsers, getPostUser, updateUserName }

