import axios from 'axios';


function loginUser(email, password) {
    return dispatch =>
        axios
            .post('https://nodejs-test-api-blog.herokuapp.com/api-docs/#/Auth/post_auth', {
                email,
                password,
            })
            .then(res => console.log(res))
            .catch(e => console.log(e))
}

export default loginUser
