import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
//import { useDispatch, useSelector} from 'react-redux';
import WithServer from '../hoc';

import './loginForm.css';


function loginUser(email, password) {
    try {
        const _response = axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", {
            email,
            password,
        });
        console.log('üëâ Returned data:', _response);
        return _response
    } catch (e) {
        console.log(`üò± Axios request failed: ${e}`);
    }

}

function bindUser(token) {
    try {
        const res = axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth/user', { headers: { "Authorization": `Bearer ${token}` } })
        localStorage.setItem('token', token)
        return res
    } catch (e) {
        console.log(e)
    }

}


const LoginForm = ({ Server }) => {

    //const dispatch = useDispatch()
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    //const state = useSelector(state => state);

    let history = useHistory();

