import React from 'react';
import { useSelector } from 'react-redux';

import './post-list.css';

let prof = "https://widgetwhats.com/app/uploads/2019/11/free-profile-photo-whatsapp-4.png";

const PostList = () => {

    const post = useSelector(state => state.post)
    const postUser = useSelector(state => state.postUser)

    let date = post.dateCreated
    let newDate = date.replace(/[T-Z]/g, ' ')
    newDate = newDate.replace(/\..*/, '')

    return (
        <div className="post-list" onClick={postUser}>
            <div className="post">
                <div className="post-list-item">
                    <div className="name">
                        <img src={prof} className="photo" alt="prof" />
                        <p> Daniil Gizatulin</p>
                        {/*<p> {user.name}</p>*/}
                    </div>
                    <div className="cards">
                        <p className="title">{post.title}</p>
                        {/*<img src={pizza} alt="img" className="post-image" />*/}
                        <ul className="full-text">{post.fullText}</ul>
                        <div className="info-post">
                            <span className="date">{newDate}</span>
                            <div className="interaction">
                                <button className='like' onClick={(e) => e.target.classList.toggle("liked")}><i className="fa fa-heart"></i></button>
                                <button className='delete'><i className="fa fa-trash-o"></i></button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default PostList;
