import React, { useEffect, useState } from 'react'

import './index.sass'

const DateAndTime = () => {

    function getZero(num) {
        if (num >= 0 && num < 10) {
            return `0${num}`;
        } else {
            return num;
        }
    }

    const [currentDate, setCurrentDate] = useState('');

    useEffect(() => {
        setInterval(() => {
            let day = new Date().getDay()
            let date = getZero(new Date().getDate()); //Current Date
            let month = getZero(new Date().getMonth() + 1); //Current Month
            let year = new Date().getFullYear(); //Current Year
            let hours = getZero(new Date().getHours()); //Current Hours
            let min = getZero(new Date().getMinutes()); //Current Minutes
            let sec = getZero(new Date().getSeconds()); //Current Seconds
            setCurrentDate(
                day + ' ' + date + '/' + month + '/' + year
                + ' ' + hours + ':' + min + ':' + sec
            );
        }, 1000)
    }, []);

    if (currentDate.length === 0) {
        return <p className='spinner-date'>waiting...</p>
    }

    return (
        <div className={'Date-Time'} >
            {/*<div className='Date'>
                Fri 04.06.2021
            </div>*/}
            <div className='Time'>
                {/*11:27:33 PM*/}
                {currentDate}
            </div>
        </div>
    )
}

export default DateAndTime
