import axios from 'axios';
import { LOGIN_USER, LOGOUT, LOADED, DELETED, ALL_USERS } from './types'

const JWTToken = localStorage.getItem('token');
if (JWTToken) {
    axios.defaults.headers['Authorization'] = `Bearer ${JWTToken}`
}

const authorizationUser = () => {
    try {
        const _res = axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth/user')
        return _res
    } catch (e) {
        console.log(e.response);
    }
}

function loginUser(email, password, setError, nav) {
    return (dispatch) =>
        axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", {
            email,
            password,
        })
            .then(res => {
                axios.defaults.headers['Authorization'] = `Bearer ${res.data.token}`
                if (nav) {
                    nav(`/list/page${1}/?limit=10&skip=${0}`)
                }
                localStorage.setItem('token', res.data.token)
                if (localStorage.getItem('token')) {
                    authorizationUser()
                        .then(res => {
                            dispatch({ type: LOGIN_USER, payload: res.data })
                            let saveUser = JSON.stringify(res.data)
                            localStorage.setItem('user', saveUser)
                        })
                }
            })
            .catch(e => {
                e.response.status === 404
                    && setError({ error: true, message: 'Такого пользователя не существует' })
                if (nav) {
                    nav('/login')
                }
            })
}

const setUser = (user) => {
    return (dispatch) =>
        dispatch({ type: LOGIN_USER, payload: user })
}

const logout = (history) => {
    return (dispatch) => {
        dispatch({ type: LOGOUT })
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        history.push("/login");
    }
}

const loaded = () => {
    return (dispatch) =>
        dispatch({ type: LOADED })
}

const deleteUser = (id, token, history) => {
    return (dispatch) => {
        axios.delete(`https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`)
            .then(res => {
                dispatch({ type: LOGOUT })
                dispatch({ type: DELETED, payload: true })
                localStorage.removeItem('user');
                localStorage.removeItem('token');
                setTimeout(() => {
                    history.push("/login")
                    dispatch({ type: DELETED, payload: false })
                }, 500)
            })
    }
}

const createNewUser = (email, password, name, history) => {
    return (dispatch) =>
        axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/users", {
            email,
            password,
            name
        })
            .then(res => setTimeout(() => history.push('/login'), 500))
            .catch(e => {
                console.log(e.response)
            })

}

const getUserAvatar = (ref, image, defaultImage, alt) => {
    axios.get(`https://nodejs-test-api-blog.herokuapp.com${image}`)
        .then(res => {
            if (res.statusText === 'OK') {
                ref.current.src = `https://nodejs-test-api-blog.herokuapp.com${image}`
            }
        })
        .catch(e => {
            if (e.status >= 400) {
                ref.current.src = defaultImage;
                ref.current.alt = alt;
            }
        })
}

const getAllUsers = () => {
    return (dispatch) =>
        axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/users')
            .then(res => {
                dispatch({ type: ALL_USERS, payload: res.data })
                dispatch({ type: LOADED })
            })


}


