const initialState = {
    error: false,
    login: false,
    user: null,
    allUsers: null,
    allPosts: [],
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'NEW_USER':
            return {
                ...state,
                user: action.payload
            }
        case 'LOGIN':
            return {
                ...state,
                login: true
            }
        case 'LOGOUT':
            return {
                ...state,
                login: false,
                user: null
            }
        case 'ERROR':
            return {
                ...state,
                error: action.payload
            }
        case 'ALL_USERS':
            return {
                ...state,
                allUsers: action.payload
            }
        case 'ALL_POSTS':
            return {
                ...state,
                allPosts: action.payload
            }
        case 'POST_ADD_TO_STATE':
            const id = action.payload;

            state.allPosts.map(item => {
                if (item._id === id) {
                    return item
                }
                return item
            })
            return {
                ...state
            };
        default:
            return state
    }
}


export default reducer;
