import axios from 'axios';
import { fault, addUser, remove, logout, loaded } from '../actions';

export default class Server {

    //!USER
    //? Create user and authorization.................................................................................................

    async loginUser(email, password, nav, dispatch) {
        await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", {
            email,
            password,
        })
            .then(res => {
                localStorage.setItem('token', res.data.token)
                if (localStorage.getItem('token')) {
                    this.authorizationUser(localStorage.getItem('token'))
                        .then(res => {
                            dispatch(addUser(res.data))
                            let saveUser = JSON.stringify(res.data)
                            localStorage.setItem('user', saveUser)
                        })
                }
            })
            .catch(e => {
                nav('/login')
                dispatch(fault())
            })

    }

    async authorizationUser(token) {
        try {
            const _res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth/user', { headers: { "Authorization": `Bearer ${token}` } })
            return _res
        } catch (e) {
            console.log(e.response);
        }
    }

    async createNewUser(email, password, name, history, dispatch) {
        try {
            await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/users", {
                email,
                password,
                name
            })
                .then(res => setTimeout(() => history.push('/login'), 500))
                .catch(e => dispatch(fault()))
        } catch (e) {
            console.log(e.response);
        }
    }

    //?Get user(s) ......................................................................................................................

    async getAllUsers() {
        try {
            const res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/users')
            return res
        } catch (e) {
            console.log(e.response);
        }
    }

    async getUser(id) {
        try {
            const res = await axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`)
            return res
        } catch (e) {
            return e.response
        }
    }


    async deleteUser(id, token, dispatch, history) {
        await axios.delete(`https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`, { headers: { "Authorization": `Bearer ${token}` } })
            .then(res => {
                dispatch(logout());
                dispatch(remove(true))
                localStorage.removeItem('user');
                localStorage.removeItem('token');
                setTimeout(() => {
                    history.push("/login")
                    dispatch(remove(false))
                }, 500)
            })
    }

    //?Update user and user avatar................................................................

    async updateUserName(id, token, name, dispatch) {
        await axios({
            method: 'PATCH',
            url: `https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`,
            data:
            {
                name,
            },

            headers:
                { "Authorization": `Bearer ${token}` }

        })
            .then(res => {
                localStorage.setItem('user', JSON.stringify(res.data))
                dispatch(addUser(res.data))
            })
    }

    async updateUserAvatar(id, token, avatar, dispatch) {
        await axios.put(`https://nodejs-test-api-blog.herokuapp.com/api/v1/users/upload/${id}`, avatar, {
            headers: {
                "Authorization": `Bearer ${token}`,
                "Content-Type": ' multipart/form-data'
            }
        }
        )
            .then(res => {
                localStorage.setItem('user', JSON.stringify(res.data))
                dispatch(addUser(res.data))
            })
    }

    //!POST
    //?Get post(s) ...........................................................................................................................

    async getAllPosts(skip = 0, limit = 10) {
        try {
            const res = await axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts?limit=${limit}&skip=${skip}`)
            return res
        } catch (e) {
            console.log(e.response)
        }
    }

    async getPost(id, refImagePost, defaultImage, dispatch) {
        try {
            const res = await axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`)
                .then(res => {
                    dispatch(loaded())
                    this.getImage(refImagePost, res.data.image, defaultImage, '')
                })
            return res
        } catch (e) {
            console.log(e.response)
        }
    }

    async deletePost(id, token, closeDeleteModal, dispatch, history) {
        await axios.delete(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`, { headers: { "Authorization": `Bearer ${token}` } })
            .then(res => {
                closeDeleteModal()
                dispatch(remove(true))
                setTimeout(() => {
                    history.push(`/list/page${1}/?limit=10&skip=${0}`)
                    dispatch(remove(false))
                }, 500)
            })
    }

    //?Create new post........................................................................

    async createNewPost(title, fullText, description, token, dispatch) {
        try {
            const res = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/posts",
                {
                    title,
                    fullText,
                    description,
                },
                {
                    headers:
                        { "Authorization": `Bearer ${token}` }
                });
            return res
        } catch (e) {
            dispatch(fault())
            console.log(e.response)
        }
    }


    //?Update post and post image................................................................

    async updatePost(id, token, title, description, fullText, history, user) {
        await axios({
            method: 'PATCH',
            url: `https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`,
            data:
            {
                title,
                description,
                fullText,
            },

            headers:
                { "Authorization": `Bearer ${token}` }

        })
            .then(res => setTimeout(() => history.push(`/user-posts/${user._id}`), 1500))
    }

    async updatePostImage(id, token, image, refImagePost) {
        await axios.put(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/upload/${id}`, image, {
            headers: {
                "Authorization": `Bearer ${token}`,
                "Content-Type": ' multipart/form-data'
            }
        }
        )
            .then(res => {
                this.getPost(id)
                    .then(res => this.getImage(refImagePost, res.data.image))
            })


    }

    //*Like post........................................................

    async setLikePost(id, token) {
        try {
            const res = await axios({
                method: 'PUT',
                url: `https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/like/${id}`,
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            })
            return res
        } catch (e) {
            console.log(e.response)
        }
    }

    //? get Image post and avatar ...................................................................................

    async getImage(ref, image, defaultImage, alt) {
        await axios.get(`https://nodejs-test-api-blog.herokuapp.com${image}`)
            .then(res => {
                if (res.statusText === 'OK') {
                    ref.current.src = `https://nodejs-test-api-blog.herokuapp.com${image}`
                }
            })
            .catch(e => {
                if (e.status >= 400) {
                    ref.current.src = defaultImage;
                    ref.current.alt = alt;
                }
            })
    }

}
