import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useHistory } from 'react-router-dom';

import './app-header.css';

const AppHeader = () => {

    let history = useHistory();

    const nav = (link) => {
        history.push(link);
    }

    const logOut = () => {
        localStorage.removeItem('token')
    }

    useEffect(() => {
        localStorage.getItem('token')

    }, [isAuthorized])

    const isAuthorized = localStorage.getItem('token')

    return (
        <header className="header">
            <Link to={'/'} className="h3 text-light">PIPITER</Link>
            <nav className="navbar navbar-light">
                <form className="form-inline" onSubmit={(e) => e.preventDefault()}>
                    <input className="form-control mr-sm-2 " type="search" placeholder="Search" aria-label="Search" />
                    <button className="btn btn-outline-secondary my-2 my-sm-0" type="submit" onClick={() => nav("/list")}>Search</button>
                </form>
            </nav>
            {!isAuthorized ?
                <button className="log btn btn-dark" onClick={() => nav("/login")}>Sign in</button> :
                <button className="log btn btn-dark" onClick={logOut}>Log out</button>
            }

        </header>
    )
}



export default AppHeader;
