import React, { useCallback, useEffect, useState } from 'react';
//import { like } from '../../actions';
import { useSelector } from 'react-redux';
import WithServer from '../hoc';
import { useHistory } from 'react-router-dom';
import { posts, setPost, setPostUser } from '../../actions';
import { useDispatch } from 'react-redux';

import './post-list-item.css';

//let pizza = "https://static.1000.menu/img/content/21458/-salat-cezar-s-kr-salat-cezar-s-krevetkami-s-maionezom_1501173720_1_max.jpg";
let prof = "https://widgetwhats.com/app/uploads/2019/11/free-profile-photo-whatsapp-4.png";


const PostListItem = ({ Server }) => {

    const allPosts = useSelector(state => state.allPosts)
    const dispatch = useDispatch()

    let history = useHistory();
    //let user = JSON.parse(localStorage.getItem('user'));

    //const getUsers = () => {
    //    Server.getAllUsers()
    //        .then(res => console.log(res.data))
    //}


    useEffect(() => {
        Server.getAllPosts()
            .then(res => {
                dispatch(posts(res.data))
            })
            .catch(er => console.log(er))
    }, [Server, dispatch])


    const pst = [];

    allPosts.forEach(p => {
        Server.getPost(p._id)
            .then(res => pst.push(res.data))
    })

    const goToUserPost = async (idPost, idUser) => {
        await Server.getPost(idPost)
            .then(res => dispatch(setPost(res.data)))
        await Server.getUser(idUser)
            .then(res => dispatch(setPostUser(res.data)))
        history.push('/post')
    }

    let idx = 0;

    const itempos = allPosts.map(postItem => {

        idx++

        return (
            <div className="post" key={idx} onClick={() => goToUserPost(postItem._id, postItem.postedBy)}>
                <div className="post-list-item">
                    <div className="name">
                        <img src={prof} className="photo" alt="prof" />
                        <p> Daniil Gizatulin</p>
                        {/*<p> {user.name}</p>*/}
                    </div>
                    <div className="cards">
                        <p className="title">{postItem.title}</p>
                        {/*<img src={pizza} alt="img" className="post-image" />*/}
                        <ul className="full-text">{postItem.description}</ul>
                        <div className="info-post">
                            {/*<span className="date">2021-04-27 18:30PM</span>*/}
                            <div className="interaction">
                                {/*<button className='like' onClick={(e) => e.target.classList.toggle("liked")}><i className="fa fa-heart"></i></button>*/}
                                {/*<button className='delete'><i className="fa fa-trash-o"></i></button>*/}
                                <button className='delete'>ds<i class="fa fa-arrow-right" aria-hidden="true"></i></button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        )
    })


    return (
        //<div className="post-list">
        //    <div className="post">
        //        <div className="post-list-item">
        //            <div className="name">
        //                <img src={prof} className="photo" alt="prof" onClick={() => console.log(allPosts)} />
        //                <p> Daniil Gizatulin</p>
        //                {/*<p> {user.name}</p>*/}
        //            </div>
        //            <div className="cards">
        //                <p className="title">Lorem ipsum ipsum ipsum</p>
        //                <img src={pizza} alt="img" className="post-image" />
        //                <ul className="full-text">Lorem ipsum dolor sit amet consectetur adipisicing elit. Accusantium odio modilabore repudiandaenatus debitis officiis nesciunt odit molestias illum cum, est expedita nihil quisquam aut at, ipsum excepturi maxime.Lorem ipsum dolor sit amet consectetur adipisicing elit. Doloremque error ad exercitationem, sitsint nulla,ratione possimus doloribus eligendi fugiat nisi animi, qui quia sunt sed deserunt facere.Temporibus, ut?  </ul>
        //                <div className="info-post">
        //                    <span className="date">20.04.2021 12:05 PM</span>
        //                    <div className="interaction">
        //                        <button className='like' onClick={(e) => e.target.classList.toggle("liked")}><i className="fa fa-heart"></i></button>
        //                        <button className='delete' onClick={getPosts}><i className="fa fa-trash-o"></i></button>
        //                    </div>
        //                </div>
        //            </div>
        //        </div>
        //    </div>
        //</div>


        <div className="post-list">
            {itempos}
        </div>

    )
}


export default WithServer()(PostListItem);
