import React, { useEffect, useRef, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
import Spinner from '../spinner';
import SpinnerImage from './spinner-image'
import ModalConfirm from '../modalConfirm';
import { useHistory } from 'react-router';

import './editor-profile.sass';

import { loaded, deleteUser } from '../../redux/user/actions'

import defaultAvatar from '../../image/no-avatar.jpg'

const EditorProfile = ({ Server }) => {

    // Modal 
    const [show, setShow] = useState(false)
    const handleClose = () => setShow(false)
    const handleShow = () => setShow(true)

    //useState
    const [name, setName] = useState('')
    const [currentUser, setUser] = useState({})
    const [loadingImage, setLoadingImage] = useState(false)
    const [errorName, setError] = useState({ error: false, message: '' })

    //History
    const history = useHistory()

    //useDispatch
    const dispatch = useDispatch()

    //useSelector
    const user = useSelector(state => state.userState.user)
    const loading = useSelector(state => state.userState.loading)

    //useRef
    const refAvatar = useRef()

    useEffect(() => {
        setUser(user)
    }, [dispatch, user])

    const isAuthorized = localStorage.getItem('token');

    const changeName = (id, token, name) => {
        if (!name) {
            setError({ error: true, message: 'Заполните поле!' })
            setTimeout(() => setError({ error: false, message: '' }), 1700)
        } else {
            Server.updateUserName(id, token, name, dispatch)
                .then(res => {
                    setUser({ ...currentUser, name })
                    setName('')
                })
        }
    }

    const updateAvatar = (e) => {
        setLoadingImage(true)
        let formData = new FormData()
        formData.append('avatar', e.target.files[0])
        Server.updateUserAvatar(user._id, isAuthorized, formData, dispatch)
            .then((res) => setLoadingImage(false))
    }

    const delUser = () => {
        dispatch(deleteUser(user._id, isAuthorized, history))
        handleClose()
    }

    //if user = true - get avatar
    //useEffect(() => {
    //    if (user._id) {
    //        Server.getImage(refAvatar, user.avatar, defaultAvatar, '')
    //        dispatch(loaded())
    //    }
    //}, [Server, dispatch, user, refAvatar])

    const [image, setImage] = useState()

    function checkImage(imageSrc, good, bad) {
        var img = new Image();
        img.src = imageSrc;
        img.onload = good;
        img.onerror = bad;
    }

    //checkImage(
    //    `https://nodejs-test-api-blog.herokuapp.com${user.avatar}`,
    //    function () { setImage(`https://nodejs-test-api-blog.herokuapp.com${user.avatar}`) },
    //    function () { setImage(defaultAvatar) }
    //)
    checkImage(
        `https://nodejs-test-api-blog.herokuapp.com${user.avatar}`,
        () => { setImage(`https://nodejs-test-api-blog.herokuapp.com${user.avatar}`) },
        () => { setImage(defaultAvatar) }
    )


    if (loading || !user || !currentUser) {
        return <Spinner />
    }
    return (
        <div className='container-editor-profile' onClick={() => console.log(user)}>
            <form className='editor-profile' onSubmit={(e) => e.preventDefault()}>
                <header className='editor-header'>
                    <div className='editor-avatar'>
                        {loadingImage ? <SpinnerImage /> : <img alt='img' ref={refAvatar} src={image} />}
                        <label htmlFor='file-upload' className='inp-upload' >
                            <i className="fa fa-camera"></i>
                            <input
                                type='file'
                                accept="image/*"
                                id="file-upload"
                                onChange={(e) => updateAvatar(e)}
                            />
                        </label>
                    </div>
                    <p className='editor-name'>{currentUser.name}</p>
                </header>
                <div className='inp-name'>
                    <label >Name:</label>
                    <input
                        type="text"
                        className={`form-control ${errorName.error && 'shadow-error'}`}
                        placeholder="Change your name"
                        title='Enter your new name'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <button className='btn btn-primary save' onClick={() => changeName(user._id, isAuthorized, name)}>Save</button>
                <button type='button' className='btn btn-danger remove' onClick={handleShow}>Delete user</button>
                {errorName.error && <p className='error-name'>{errorName.message}</p>}
            </form>
            <ModalConfirm show={show} handleClose={handleClose} remove={delUser} label={'Are you sure , what you want to delete the user?'} />
        </div>
    )
}

export default WithServer()(EditorProfile);
