import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useHistory } from 'react-router-dom';
import { logout, removeToken } from '../../actions';

import './app-header.css';

const AppHeader = () => {
    const login = useSelector(state => state.login)

    const dispatch = useDispatch()

    let history = useHistory();
    const nav = (link) => {
        history.push(link);
    }

    const logOut = () => {
        dispatch(logout());
        dispatch(removeToken())
        localStorage.removeItem('user')
        localStorage.removeItem('token')
        nav("/login")
    }

    //const isAuthorized = localStorage.getItem('token')
    //isAuthorizedToken ? dispatch(login()) : dispatch(logout());

    const Button = () => {
        const isAuthorized = localStorage.getItem('token')
        if (login && isAuthorized) {
            return (<button className="log btn btn-dark" onClick={logOut}>Log out</button>)
        } else {
            return (<button className="log btn btn-dark" onClick={() => nav("/login")}>Sign in</button>)
        }
    }

    return (
        <header className="header">
            <Link to={'/'} className="h3 text-light">PIPITER</Link>
            <nav className="navbar navbar-light">
                <form className="form-inline" onSubmit={(e) => e.preventDefault()}>
                    <input className="form-control mr-sm-2 " type="search" placeholder="Search" aria-label="Search" />
                    <button className="btn btn-outline-secondary my-2 my-sm-0" type="submit" onClick={() => nav('/list')}>Search</button>
                </form>
            </nav>
            {/*{
                isAuthorized ?
                    (<button className="log btn btn-dark" onClick={logOut}>Log out</button>) :
                    (<button className="log btn btn-dark" onClick={() => nav("/login")}>Sign in</button>)
            }*/}
            <Button />
        </header>
    )
}



export default AppHeader;
