import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
import { useHistory, useParams } from 'react-router-dom';
import { posts, loaded } from '../../actions';
import Spinner from '../spinner';
import SimpleSnackbar from '../snackbar';

import './post-list-item.css';


const PostListItem = ({ Server }) => {
    //useParams
    const { skip } = useParams();

    //useSelector
    const postOnPage = useSelector(state => state.postOnPage)
    const loading = useSelector(state => state.loading)
    const isSnackbar = useSelector(state => state.isSnackbar)

    //useDispatch
    const dispatch = useDispatch()

    //useState
    const [countBtn, setCountBtn] = useState([]);
    const [activeNum, setActiveNum] = useState(+skip)

    //useHistory
    const history = useHistory();

    const navHistory = (url) => {
        history.push(url)
    }

    useEffect(() => {
        Server.getAllPosts(skip)
            .then(res => {
                dispatch(posts(res.data))
                dispatch(loaded())
            })
            .catch(er => console.log(er))
    }, [Server, dispatch, skip])

    useEffect(() => {
        if (!loading) {
            Server.getAllPosts(0, 0)
                .then(res => {
                    setCountBtn(res.data.filter((item, i) => res.data[i / 5]))
                })
        } else {
            return
        }
    }, [Server, loading])


    let idx = 0;

    const postList = postOnPage.map((postItem, i) => {
        idx++

        return (
            <div className="post" key={idx}>
                <div className="post-list-item" >
                    <div className="cards">
                        <p className="title">{postItem.title}</p>
                        <ul className="full-text">{postItem.description}</ul>
                        <div className="full-post">
                            <button
                                className='view'
                                onClick={() => navHistory(`/post/${postItem._id}`)}>
                                {/*<i className="fa fa-arrow-circle-right fa-2x"></i>*/}
                                View
                            </button>
                        </div>
                    </div>
                </div>
            </div>

        )
    })


    return (
        <>
            <div className="post-list">
                {postList.length === 0 && <p className='not-found-message'>Not found...</p>}
                {
                    !loading
                        ? postList
                        : <Spinner />
                }
            </div>
            <div className='nav-posts'>
                <div className='nav-posts-overflow'>
                    {countBtn.map((item, i) => {
                        return <button
                            key={idx++}
                            className={`btn-nav-posts ${activeNum === i * 5 ? 'active-btn' : ''}`}
                            onClick={(e) => {
                                setActiveNum(i * 5)
                                navHistory(`/list/${i * 5}`)
                            }}
                        >{i + 1}</button>
                    })}
                </div>
            </div>
            <SimpleSnackbar isSnackbar={isSnackbar} color={"success"} message={'Deleted successful!'} />
        </>
    )
}


export default WithServer()(PostListItem);
