import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useHistory } from 'react-router-dom';
import { login, logout } from '../../actions';

import './app-header.css';

const AppHeader = () => {
    const isAuthorized = useSelector(state => state.isAuthorized)
    let user = useSelector(state => state.user)

    const dispatch = useDispatch()

    let history = useHistory();
    const nav = (link) => {
        history.push(link);
    }

    const logOut = () => {
        //dispatch(logout());
        //user = null
        localStorage.removeItem('token')
        nav("/login")
    }

    const isAuthorizedToken = localStorage.getItem('token')
    isAuthorizedToken ? dispatch(login()) : dispatch(logout());


    return (
        <header className="header">
            <Link to={'/'} className="h3 text-light">PIPITER</Link>
            <nav className="navbar navbar-light">
                <form className="form-inline" onSubmit={(e) => e.preventDefault()}>
                    <input className="form-control mr-sm-2 " type="search" placeholder="Search" aria-label="Search" />
                    <button className="btn btn-outline-secondary my-2 my-sm-0" type="submit" onClick={() => nav("/list")}>Search</button>
                </form>
            </nav>
            {/*{!isAuthorized ?*/}
            {user ?
                <button className="log btn btn-dark" onClick={logOut}>Log out</button> :
                <button className="log btn btn-dark" onClick={() => {
                    //dispatch(login())
                    console.log(user)
                    nav("/login")
                }}>Sign in</button>

            }
        </header>
    )
}



export default AppHeader;
