import React from 'react';
import { useHistory } from 'react-router-dom';
//import axios from 'axios';
import WithServer from '../hoc';

import './loginForm.css';


const LoginForm = ({ props }) => {

    let history = useHistory();

    const nav = (link) => {
        history.push(link)
    }

    let signInUsers = {
        email: '',
        password: ''
    }

    const emailUser = (e) => {
        signInUsers.email = e.target.value
    }

    const passwordUser = (e) => {
        signInUsers.password = e.target.value
    }

    //async function signInUser(data) {
    //    try {
    //        const _response = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", data);
    //        console.log('👉 Returned data:', _response);
    //        if (_response.statusText === "OK") {
    //            nav("/list");
    //        }
    //    } catch (e) {
    //        console.log(`😱 Axios request failed: ${e}`);
    //    }
    //}

    async function signInUser(data) {
        const { Server } = this.props;
        Server.signInUser(data)

    }

    return (
        <div className="post-list">
            <div className="modal-authorization">
                <form>
                    <div className="form-group">
                        <label>Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            title='Enter your email'
                            onChange={emailUser} />
                        <small className="form-text text-muted"></small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            title='Enter your password'
                            onChange={passwordUser} />
                        <div className="auth-group">
                            <button className="forgotPass authHover" onClick={(e) => e.preventDefault()}>Forgot password</button>
                            <button className="signUp authHover" onClick={() => nav("/signUp")}>sign up</button>
                        </div>

                        <small className="form-text text-muted"></small>
                    </div>
                    <button type="submit" className="btn btn-outline-primary cancel" onClick={() => nav("/")}>Сancel</button>
                    <button type="submit" className="btn btn-primary confirm" onClick={(e) => {
                        e.preventDefault();
                        signInUser(signInUsers)
                    }}>Sign in</button>
                </form>
            </div>
        </div>
    )
}


export default WithServer()(LoginForm);
