const initialState = {
    error: false,
    login: false,
    user: null,
    allUsers: null,
    allPosts: [],
    post: null,
    postUser: null,
    loading: false
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'NEW_USER':
            return {
                ...state,
                user: action.payload
            }
        case 'LOGIN':
            return {
                ...state,
                login: true
            }
        case 'LOGOUT':
            return {
                ...state,
                login: false,
                user: null
            }
        case 'ERROR':
            return {
                ...state,
                error: action.payload
            }
        case 'ALL_USERS':
            return {
                ...state,
                allUsers: action.payload
            }
        case 'ALL_POSTS':
            if (state.allPosts === []) {
                return {
                    ...state,
                    loading: true
                }
            } else {
                return {
                    ...state,
                    allPosts: action.payload,
                    loading: false
                }
            }
        case 'POST':
            return {
                ...state,
                post: action.payload
            }
        case 'POST_USER':
            return {
                ...state,
                postUser: action.payload
            }
        default:
            return state
    }
}


export default reducer;
