const initialState = {
    error: false,
    login: false,
    user: null,
    allUsers: null,
    allPosts: [],
    fullPost: []
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'NEW_USER':
            return {
                ...state,
                user: action.payload
            }
        case 'LOGIN':
            return {
                ...state,
                login: true
            }
        case 'LOGOUT':
            return {
                ...state,
                login: false,
                user: null
            }
        case 'ERROR':
            return {
                ...state,
                error: action.payload
            }
        case 'ALL_USERS':
            return {
                ...state,
                allUsers: action.payload
            }
        case 'ALL_POSTS':
            return {
                ...state,
                allPosts: action.payload
            }
        case 'POST':
            const id = action.payload;
            const item = state.allPosts.find(i => i._id === id)
            const newItem = {
                title: item.title,
                fullText: item.fullText,
                description: item.description,
                dateCreated: item.dateCreated,
                postedBy: item.postedBy,
                __v: item.__v,
                image: item.image,
                _id: item._id,
                likes: item.likes
            }
            return {
                ...state,
                fullPost: [...newItem]
            }
        default:
            return state
    }
}


export default reducer;
