import React, { useEffect, useRef, useState } from 'react';
import { useSelector } from 'react-redux';
import WithServer from '../hoc';

import prof from './no-avatar.jpg'
import deletProf from './delete-user.jpg'
import './post-list.css';

const PostList = ({ Server }) => {
    const ref = useRef()
    const post = useSelector(state => state.post)
    const postUser = useSelector(state => state.postUser)

    const [heart, setHeart] = useState(post.likes.length)

    let date = post.dateCreated
    let newDate = date.replace(/[T-Z]/g, ' ')
    newDate = newDate.replace(/\..*/, '')

    const isAuthorized = localStorage.getItem('token');

    async function redHeart(likedUser, e) {
        const someUser = await JSON.parse(localStorage.getItem('user'))
        const idxLike = await likedUser.indexOf(someUser._id)

        if (idxLike >= 0) {
            console.log('user true')
            e.target.classList.add('liked')
        } else {
            console.log('user false')
            e.target.classList.remove('liked')
        }

    }

    useEffect(() => {
        Server.getPost(post._id)
            .then(res => {
                redHeart(res.data.likes, ref.current)
            })
    }, [Server, post._id])

    const postLike = async (e) => {
        await Server.getPost(post._id)
            .then(res => {
                if (res.data.likes.length !== heart) {
                    setHeart(res.data.likes.length)
                    redHeart(res.data.likes, e)
                }
            })
    }

    async function setLike(id, token, e) {
        await Server.setLikePost(id, token)
        postLike(e)
    }



    return (
        <div className="item" onClick={() => console.log(ref.current)}>
            <div className="item-post">
                <div className="post-item-list">
                    {postUser
                        ? <div className="info-user">
                            <img src={prof} className="user-avatar" alt="prof" />
                            <p> {postUser.name}</p>
                        </div>
                        : <div className="info-user">
                            <img src={deletProf} className="user-avatar" alt="prof" />
                            <p className="delete-user"> User has been deleted</p>
                        </div>}
                    <div className="info-post">
                        <p className="title">{post.title}</p>
                        {/*<img src={pizza} alt="img" className="post-image" />*/}
                        <ul className="full-text">{post.fullText}</ul>
                        <div className="footer-post">
                            <span className="date">{newDate}</span>
                            <div className="interaction">
                                <button
                                    ref={ref}
                                    className='like'
                                    onClick={(e) => {
                                        if (isAuthorized) {
                                            setLike(post._id, isAuthorized, e)
                                            //e.target.classList.toggle("liked")
                                        } else {
                                            alert('Login first')
                                        }
                                    }
                                    }><i className="fa fa-heart"></i></button>{heart}
                                {/*<button className='delete'><i className="fa fa-trash-o"></i></button>*/}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default WithServer()(PostList);
