//import axios from 'axios';
import { POSTS, LOADED, ERROR, DELETED, POST, ALL_POSTS, FULL_POST } from './types'

import api from '../../services/api';

//const JWTToken = localStorage.getItem('token');
//if (JWTToken) {
//    axios.defaults.headers['Authorization'] = `Bearer ${JWTToken}`
//}

const setPosts = (posts) => {
    return (dispatch) =>
        dispatch({ type: POSTS, payload: posts })
}

const getPosts = (skip = 0, limit = 10) => {
    return (dispatch) =>
        api.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts?limit=${limit}&skip=${skip}`)
            .then(res => {
                dispatch({ type: POSTS, payload: res.data })
                dispatch({ type: LOADED })
            })
            .catch(e => dispatch({ type: ERROR }))

}

const getAllPosts = (skip = 0, limit = 10) => {
    return (dispatch) =>
        api.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts?limit=${limit}&skip=${skip}`)
            .then(res => {
                dispatch({ type: ALL_POSTS, payload: res.data })
                dispatch({ type: LOADED })
            })
}

const loaded = () => {
    return (dispatch) =>
        dispatch({ type: LOADED })
}

const fault = () => {
    return (dispatch) =>
        dispatch({ type: ERROR })
}

const deletePost = (id, token, history) => {
    return (dispatch) =>
        api.delete(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`)
            .then(res => {
                dispatch({ type: DELETED, payload: true })
                setTimeout(() => {
                    history.push(`/list/page${1}/?limit=10&skip=${0}`)
                    dispatch({ type: DELETED, payload: false })
                }, 500)
            })
    //.catch(e => )

}

const getPost = (id) => {
    return (dispatch) =>
        api.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`)
            .then(res => {
                dispatch({ type: POST, payload: res.data })
                dispatch({ type: LOADED })
            })
            .catch(e => dispatch({ type: ERROR }))
}

const getFullPost = (id) => {
    return (dispatch) =>
        api.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`)
            .then(res => {
                dispatch({ type: FULL_POST, payload: res.data })
                dispatch({ type: LOADED })
            })
            .catch(e => dispatch({ type: ERROR }))
}

const setLikePost = (id) => {
    api({
        method: 'PUT',
        url: `https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/like/${id}`,
    })

}
