import { LOGIN_USER, ERROR, LOGOUT, LOADED } from './types'

const initialState = {
    user: {},
    error: false,
    loading: true
}

function userReducer(state = initialState, action) {
    switch (action.type) {
        case LOGIN_USER:
            return {
                ...state,
                user: action.payload
            }
        case ERROR:
            return {
                ...state,
                error: true
            }
        case LOGOUT:
            return {
                ...state,
                user: {}
            }
        case LOADED:
            return {
                ...state,
                loading: false
            }
        default: return state;
    }

}

export default userReducer;
