import axios from 'axios';
import { LOGIN_USER, ERROR, LOGOUT, LOADED } from './types'

const authorizationUser = (token) => {
    try {
        const _res = axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth/user', { headers: { "Authorization": `Bearer ${token}` } })
        return _res
    } catch (e) {
        console.log(e.response);
    }
}

function loginUser(email, password, nav) {
    return (dispatch) =>
        axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", {
            email,
            password,
        })
            .then(res => {
                //nav(`/list/page${1}/?limit=10&skip=${0}`)
                localStorage.setItem('token', res.data.token)
                if (localStorage.getItem('token')) {
                    authorizationUser(localStorage.getItem('token'))
                        .then(res => {
                            dispatch({ type: LOGIN_USER, payload: res.data })
                            let saveUser = JSON.stringify(res.data)
                            localStorage.setItem('user', saveUser)
                        })
                }
            })
            .catch(e => {
                nav('/login')
                dispatch({ type: ERROR })
            })
}

const setUser = (user) => {
    return (dispatch) =>
        dispatch({ type: LOGIN_USER, payload: user })
}

const logout = (history) => {
    return (dispatch) => {
        dispatch({ type: LOGOUT })
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        history.push("/login");
    }
}

const loaded = () => {
    return (dispatch) =>
        dispatch({ type: LOADED })
}



export { loginUser, setUser, logout, loaded }
