import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import WithServer from '../hoc';
//import { addUser, fault } from '../../actions';
import SimpleSnackbar from '../snackbar';
import Error from '../error';

import './loginForm.sass';

import { loginUser } from '../../redux/user/actions'


const LoginForm = ({ show, setShow }) => {
    //useState
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    //useDispatch
    const dispatch = useDispatch()

    //useHistory
    let history = useHistory();

    //useSelector
    const error = useSelector(state => state.userState.error)
    const deleteUser = useSelector(state => state.userState.deleteUser)

    const nav = (link) => {
        history.push(link)
    }

    const [errorLogin, setError] = useState({ error: false, message: '' })

    const loginError = () => {
        if (email.length === 0 && password) {
            setError({ error: true, message: 'Введите email!' })
        }
        if (password.length === 0 && email) {
            setError({ error: true, message: 'Введите пароль!' })
        }
        if (!email && !password) {
            setError({ error: true, message: 'Заполните все поля!' })
        }
        setTimeout(() => setError({ error: false }), 1500)
    }

    const customLogin = (e) => {
        e.preventDefault()
        if (show) {
            dispatch(loginUser(email, password))
            setShow(false)
        } else {
            dispatch(loginUser(email, password, nav))
        }
        loginError()
    }

    const cancelEvent = () => {
        if (show) {
            setShow(false)
        } else {
            nav(`/list/page${1}/?limit=10&skip=${0}`)
        }
    }


    //if (error) {
    //    return <Error />
    //}



    return (
        <div className={`login-form`} onClick={() => console.log(errorLogin)}>
            <div className={`modal-authorization ${show && 'back-shadow'}`}>
                <form onSubmit={(e) => customLogin(e)} id='sign'>
                    <div className="form-group">
                        <label>Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            title='Enter your email'
                            value={email}
                            onChange={(e) => setEmail(e.target.value)} />
                        <small className="form-text text-muted"></small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            title='Enter your password'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)} />
                        <div className="auth-group">
                            <a className="forgotPass authHover" href=' '>Forgot password</a>
                            <a className="signUp authHover" href='/signUp'>sign up</a>
                        </div>

                        <small className="form-text text-muted"></small>
                    </div>
                    <input className="cancel" type='button' onClick={() => cancelEvent()} value='Сancel' />
                    <input htmlFor='sign' type="submit" className="confirm" value="Sign In" />
                    {errorLogin.error && <p className='error'>{errorLogin.message}</p>}
                </form>
            </div>
            <SimpleSnackbar isSnackbar={deleteUser} color={'success'} message={'Deleted successful!'} />
        </div >
    )
}


export default WithServer()(LoginForm);
