import React, { useEffect, useRef, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
import ModalAuthorized from './modalAuthorized'
import { useHistory, useParams } from 'react-router';
import Spinner from '../spinner';
import Error from '../error';
import { loaded, fault, getFullPost } from '../../redux/posts/actions'

import prof from '../../image/no-avatar.jpg'
import deletProf from '../../image/delete-user.jpg'
import defaultImage from '../../image/default-image.jpg'

import './post-list.sass';

const PostList = ({ Server }) => {
    //Hook

    // useState
    const [post, setPost] = useState({});
    const [postUser, setPostUser] = useState({});
    const [date, setDate] = useState('');
    const [heart, setHeart] = useState('');
    const [showModal, setShowModal] = useState('')
    const [showLike, setShowLike] = useState();

    //useDispatch & useSelector
    const dispatch = useDispatch()
    //new
    const loading = useSelector(state => state.postState.loading)
    const error = useSelector(state => state.postState.error)

    // useHistory & useParams
    const history = useHistory()
    const { id } = useParams()

    //Ref
    const refHeart = useRef()
    const refAvatar = useRef()
    const refImagePost = useRef()

    //local
    const isAuthorized = localStorage.getItem('token');
    const isAuthorizedUser = JSON.parse(localStorage.getItem('user'))

    //DateCreated
    const dateСorrector = (postData) => {
        let date = postData
        let newDate = date.replace(/[T-Z]/g, ' ')
        setDate(newDate.replace(/\..*/, ''))
    }

    // If an authorized user has a like on a post

    function saveLike(likedUser, e) {
        const idxLike = likedUser.indexOf(isAuthorizedUser._id)
        if (idxLike >= 0) {
            setShowLike(true)
        } else {
            setShowLike(false)
        }
    }

    // Func get post and user
    //useEffect(() => {
    //    Server.getPost(id)
    //        .then(res => {
    //            dispatch(loaded())
    //            setPost(res.data)
    //            setHeart(res.data.likes.length)
    //            if (isAuthorized) {
    //                saveLike(res.data.likes, refHeart.current)
    //            }
    //            dateСorrector(res.data.dateCreated)
    //            Server.getUser(res.data.postedBy)
    //                .then(res => {
    //                    setPostUser(res.data)
    //                })
    //        })
    //        .catch(() => dispatch(fault()))
    //    //eslint-disable-next-line react-hooks/exhaustive-deps
    //}, [Server, id])
    //!TEST>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>
    const fullPost = useSelector(state => state.postState.fullPost)
    const postUserState = useSelector(state => state.userState.postUser)
    const [userError, setError] = useState(false)
    useEffect(() => {
        dispatch(getFullPost(id))
        if (fullPost._id) {
            setPost(fullPost)
            setHeart(fullPost.likes.length)
            if (isAuthorized) {
                saveLike(fullPost.likes, refHeart.current)
            }
            dateСorrector(fullPost.dateCreated)
            if (!userError) {
                Server.getUser(fullPost.postedBy)
                    .then(res => setPostUser(res.data))
                    .catch(e => setError(true))
            }
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [fullPost, id, isAuthorized])
    //!TEST>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>

    // number of likes on the post
    const postLike = (e) => {
        Server.getPost(post._id)
            .then(res => {
                if (res.data.likes.length !== heart) {
                    setHeart(res.data.likes.length)
                    saveLike(res.data.likes, e)
                }
            })
    }

    // func setlike 
    async function setLike(id, token, e) {
        await Server.setLikePost(id, token)
        postLike(e)
    }

    //!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
    const [image, setImage] = useState({ userAvatar: prof, postImage: defaultImage })
    function checkImage(imageSrc, good, bad) {
        const img = new Image();
        img.src = imageSrc;
        img.onload = good;
        img.onerror = bad;
    }

    useEffect(() => {
        if (postUser._id) {
            checkImage(
                `https://nodejs-test-api-blog.herokuapp.com${postUser.avatar}`,
                () => { setImage({ ...image, userAvatar: `https://nodejs-test-api-blog.herokuapp.com${postUser.avatar}` }) },
                () => { setImage({ ...image, userAvatar: prof }) }
            )
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [postUser._id])
    useEffect(() => {
        if (post._id) {
            checkImage(
                `https://nodejs-test-api-blog.herokuapp.com${post.image}`,
                () => { setImage({ ...image, postImage: `https://nodejs-test-api-blog.herokuapp.com${post.image}` }) },
                () => { setImage({ ...image, postImage: defaultImage }) }
            )
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [post._id])
    //!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!

    if (loading || !post) {
        return <Spinner />
    }

    if (error) {
        return <Error message={'No such post'} />
    }

    return (
        <div className="item" onClick={() => console.log(postUserState)}>
            <div className="item-post">
                <div className="post-item-list">
                    {!userError
                        ?
                        <div className="info-user">
                            <img src={image.userAvatar} className="post-user-avatar cntr" alt="prof" ref={refAvatar} />
                            <p> {postUser.name}</p>
                        </div>
                        :
                        <div className="info-user">
                            <img src={deletProf} className="post-user-avatar cntr" alt="prof" />
                            <p className="delete-user"> User has been deleted</p>
                        </div>}
                    <div className="info-post">
                        <div className='header-post'>
                            <p className="title">{post.title}</p>

                            {isAuthorized && post.postedBy === isAuthorizedUser._id &&
                                //<button className='btn-update-post ' onClick={() => history.push(`/editor-posts/${id}`)}>
                                //    Edit post
                                //</button>}
                                <button className='btn-update-post ' onClick={() => history.push(`/post-page/${id}`)}>
                                    Edit post
                            </button>}

                        </div>
                        <img src={image.postImage} alt={post.description} className="post-image" ref={refImagePost} />
                        <p>{post.description}</p>
                        <ul className="full-text">{post.fullText}</ul>
                        <div className="footer-post">
                            <span className="date">DATE CREATE : <br />{date}</span>
                            <div className="interaction">
                                <button
                                    className='like'
                                    onClick={() => {
                                        isAuthorized
                                            ? setLike(post._id, isAuthorized, refHeart.current)
                                            : setShowModal('show')
                                    }
                                    }><i ref={refHeart} className={`fa fa-heart ${showLike ? 'liked' : 'offLiked'}`}></i></button>{heart}
                            </div>
                        </div>
                    </div>

                </div>

            </div>
            <ModalAuthorized showModal={showModal} setShowModal={setShowModal} />
        </div>
    )
}


export default WithServer()(PostList);
