//import axios from 'axios';
import { POSTS, LOADED } from './types'

const posts = (posts) => {
    return (dispatch) =>
        dispatch({ type: POSTS, payload: posts })
}

const loaded = () => {
    return (dispatch) =>
        dispatch({ type: LOADED })
}

export { posts, loaded }
