import axios from 'axios';
import { POSTS, LOADED, ERROR, DELETED, POST, ALL_POSTS } from './types'

//import { getUsers } from '../../api';

const setPosts = (posts) => {
    return (dispatch) =>
        dispatch({ type: POSTS, payload: posts })
}

const getPosts = (skip = 0, limit = 10) => {
    return (dispatch) =>
        axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts?limit=${limit}&skip=${skip}`)
            .then(res => {
                dispatch({ type: POSTS, payload: res.data })
                dispatch({ type: LOADED })
            })
            .catch(e => dispatch({ type: ERROR }))

}

const getAllPosts = (skip = 0, limit = 10) => {
    return (dispatch) =>
        axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts?limit=${limit}&skip=${skip}`)
            .then(res => {
                dispatch({ type: ALL_POSTS, payload: res.data })
                dispatch({ type: LOADED })
            })
}

const loaded = () => {
    return (dispatch) =>
        dispatch({ type: LOADED })
}

const fault = () => {
    return (dispatch) =>
        dispatch({ type: ERROR })
}

const deletePost = (id, token, history) => {
    return (dispatch) =>
        axios.delete(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`, { headers: { "Authorization": `Bearer ${token}` } })
            .then(res => {
                dispatch({ type: DELETED, payload: true })
                setTimeout(() => {
                    history.push(`/list/page${1}/?limit=10&skip=${0}`)
                    dispatch({ type: DELETED, payload: false })
                }, 500)
            })

}

const getPost = (id) => {
    return (dispatch) =>
        axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`)
            .then(res => {
                dispatch({ type: POST, payload: res.data })
                dispatch({ type: LOADED })
            })
            .catch(e => dispatch({ type: ERROR }))
}

//const getAllUsers = () => {
//    getUsers()
//        .then(res => console.log(res))
//}

const getPostImage = (ref, image, defaultImage, alt) => {
    axios.get(`https://nodejs-test-api-blog.herokuapp.com${image}`)
        .then(res => {
            if (res.statusText === 'OK') {
                ref.current.src = `https://nodejs-test-api-blog.herokuapp.com${image}`
            }
        })
        .catch(e => {
            if (e.status >= 400) {
                ref.current.src = defaultImage;
                ref.current.alt = alt;
            }
        })
}

export { getPosts, setPosts, loaded, fault, deletePost, getPost, getAllPosts, getPostImage }
