const initialState = {
    user: null,
    userToken: null
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        //case 'NEW_USER':
        //    return {
        //        ...state,
        //        user: action.payload
        //    }
        case 'NEW_USER':
            if (localStorage.getItem('token')) {
                return {
                    user: action.payload
                }
            }
            return {
                ...state,
                user: null
            }
        case 'LOGOUT':
            return {
                ...state,
                user: null
            }
        case 'ADD_TOKEN':
            return {
                ...state,
                userToken: action.payload
            }
        case 'REMOVE_TOKEN':
            return {
                ...state,
                userToken: null
            }
        default:
            return state
    }
}


export default reducer;
