import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
import { addUser } from '../../actions';

import './editor-profile.sass';

const EditorProfile = ({ Server }) => {
    const defaultAvatar = 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'

    const dispatch = useDispatch()
    const [name, setName] = useState('')
    const [currentUser, setUser] = useState({})
    const user = useSelector(state => state.user)

    useEffect(() => setUser(currentUser), [currentUser])
    console.log(currentUser)

    const isAuthorized = localStorage.getItem('token');
    //const isAuthorizedUser = JSON.parse(localStorage.getItem('user'))

    const changeName = (id, token, name) => {
        //spread operator
        setUser({ ...currentUser, name })
        Server.updateUserName(id, token, name)

        //.then(res => {
        //    let saveUser = JSON.stringify(res.data)
        //    localStorage.setItem('user', saveUser)
        //})
    }

    //useEffect(() => {
    //    if (isAuthorizedUser.name !== user.name) {
    //        //get
    //        dispatch(addUser(isAuthorizedUser))
    //    }

    //}, [dispatch, isAuthorizedUser, user.name])

    return (
        <div className='container-editor-profile' onClick={() => console.log(!!user)} >
            <form className='editor-profile' onSubmit={(e) => e.preventDefault()}>
                <header className='editor-header'>
                    <div className='editor-avatar'>
                        <img alt='img' src={defaultAvatar} />
                        <label htmlFor='file-upload' className='inp-upload' >
                            <i className="fa fa-camera"></i>
                            <input type='file' id="file-upload" />
                        </label>
                    </div>
                    <p className='editor-name'>{user.name}</p>
                </header>
                <div>
                    <label >Name:</label>
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Change your name"
                        title='Enter your new name'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <button className='btn btn-primary save' onClick={() => changeName(user._id, isAuthorized, name)}>Save</button>
            </form>
        </div>
    )
}

export default WithServer()(EditorProfile);
