import axios from 'axios';

export default class Server {

    async loginUser(data) {
        //let _token = '';

        try {
            const _response = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", data);
            console.log('👉 Returned data:', _response);
            //_token = _response.data.token;
            //axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth/user', { headers: { "Authorization": `Bearer ${_token}` } })
            //    .then(res => {
            //        console.log(res.data)
            //        localStorage.setItem(`${res.data._id}`, _token)
            //    })
            return _response
        } catch (e) {
            console.log(`😱 Axios request failed: ${e}`);
        }

    }

    async giveUser(token) {
        try {
            const res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth/user', { headers: { "Authorization": `Bearer ${token}` } })
            //.then(res => {
            console.log(res)
            localStorage.setItem(`${res.data._id}`, token)
            //})
            return res
        } catch (e) {
            console.log(e)
        }

    }

    async createNewUser(data) {
        try {
            const _response = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/users", data);
            //console.log('👉 Returned data:', _response);
            return _response
        } catch (e) {
            //console.log(`😱 Axios request failed: ${e}`);
        }
    }
}
