import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import WithServer from '../hoc';

import './registrationForm.css';

const RegistrationForm = ({ Server }) => {
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    let history = useHistory();

    const nav = (link) => {
        history.push(link)
    }

    //let user = {
    //    name: '',
    //    email: '',
    //    password: ''
    //}

    //const nameUser = (e) => {
    //    user.name = e.target.value
    //}
    //const emailUser = (e) => {
    //    user.email = e.target.value
    //}

    //const passwordUser = (e) => {
    //    user.password = e.target.value
    //}

    return (
        <div className="post-list">
            <div className="modal-registration">
                <form>
                    <div className="form-group">
                        <label>Name</label>
                        <input
                            type="name"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter name"
                            value={name}
                            onChange={setName} />
                        <small className="form-text text-muted">What is your name ?</small>
                    </div>
                    <div className="form-group">
                        <label >Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            value={setEmail}
                            onChange={emailUser} />
                        <small className="form-text text-muted">We'll never share your email with anyone else.</small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            value={password}
                            onChange={setPassword} />
                        <small className="form-text text-muted">Password must be at least 5 characters. <br /> We will
                 never share your password with anyone.</small>
                    </div>
                    <button type="submit" className="btn btn-primary" onClick={(e) => {
                        e.preventDefault();
                        Server.createNewUser(email, password, name)
                            .then(resp => {
                                if (resp.statusText === "OK") {
                                    nav("/login")
                                }
                            })
                    }}>Submit</button>
                    {/*<label className='message'>su12c</label>*/}
                </form>
            </div>
        </div>
    )
}

export default WithServer()(RegistrationForm);
