import React, { useEffect, useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory, useParams } from 'react-router';
import WithServer from '../hoc';
import Spinner from '../spinner';
import { loaded } from '../../actions';


const UserPosts = ({ Server }) => {
    const refPostsContainer = useRef()

    //useSelectors
    const loading = useSelector(state => state.loading)
    //const allPosts = useSelector(state => state.allPosts)

    //useDispatch
    const dispatch = useDispatch()

    //useState
    const [allPosts, setAllPosts] = useState([])

    //useHistory
    const history = useHistory()

    //useParams
    const { id } = useParams()

    const goToUserPost = async (idPost) => {
        history.push(`/post/${idPost}`)
    }

    useEffect(() => {
        Server.getAllPosts(0, 0)
            .then(res => {
                setAllPosts(res.data)
                dispatch(loaded())
            })
    }, [Server, dispatch])

    let idx = 0;

    // eslint-disable-next-line array-callback-return
    const postList = allPosts.map((postItem, i) => {
        idx++

        if (postItem.postedBy === id) {
            return (
                <div className="post" key={idx}>
                    <div className="post-list-item" >
                        <div className="cards">
                            <p className="title">{postItem.title}</p>
                            <ul className="full-text">{postItem.description}</ul>
                            <div className="full-post">
                                <button
                                    className='view'
                                    onClick={() => goToUserPost(postItem._id)}>
                                    View
                                </button>
                            </div>
                        </div>
                    </div>
                </div>

            )
        }
    })

    return (
        <div className="post-list" ref={refPostsContainer}>
            {/*{postList.length === 0 && <p className='not-found-message'>You don't have any posts yet...</p>}*/}
            {
                loading
                    ? postList
                    : <Spinner />
            }
        </div>
    )
}

export default WithServer()(UserPosts);
