import { LOGIN_USER, ERROR } from './types'

const initialState = {
    user: {},
    error: false,
}

function authReducer(state = initialState, action) {
    switch (action.type) {
        case LOGIN_USER:
            return {
                ...state,
                user: action.payload
            }
        case ERROR:
            return {
                ...state,
                error: true
            }
        default: return state;
    }

}

export default authReducer;
