import React from 'react';
//import { useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import './registrationForm.css';

const RegistrationForm = () => {

    //let registrationForm = useSelector((state) => state.registrationForm)


    //let user = {
    //    name: 'Daniil',
    //    email: 'dagiz@list.ru',
    //    password: '123123'
    //}

    let history = useHistory();

    let user = {
        name: '',
        email: '',
        password: ''
    }

    const nameUser = (e) => {
        user.name = e.target.value
    }
    const emailUser = (e) => {
        user.email = e.target.value
    }

    const passwordUser = (e) => {
        user.password = e.target.value
    }

    async function newUser(data) {
        try {
            const _response = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/users", data);
            console.log('ðŸ‘‰ Returned data:', _response);
            if (_response.statusText === "OK") {
                history.push('/login')
            }
        } catch (e) {
            console.log(`ðŸ˜± Axios request failed: ${e}`);
        }
    }

    const resForReg = () => {
        axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/users')
            .then(resp => {
                console.log(resp);
            })
    }


    return (
        <div className="post-list">
            <div className="modal-registration">
                <form>
                    <div className="form-group">
                        <label>Name</label>
                        <input type="name" className="form-control" aria-describedby="emailHelp"
                            placeholder="Enter name" onChange={nameUser} />
                        <small className="form-text text-muted">What is your name ?</small>
                    </div>
                    <div className="form-group">
                        <label >Email address</label>
                        <input type="email" className="form-control" aria-describedby="emailHelp"
                            placeholder="Enter email" onChange={emailUser} />
                        <small className="form-text text-muted">We'll never share your email with anyone else.</small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input type="password" className="form-control" placeholder="Password" onChange={passwordUser} />
                        <small className="form-text text-muted">Password must be at least 5 characters. <br /> We will
                 never share your password with anyone.</small>
                    </div>
                    <button type="submit" className="btn btn-primary" onClick={(e) => {
                        e.preventDefault();
                        newUser(user)
                        resForReg();
                        //registrationForm.push(user)
                        //console.log(registrationForm)
                    }}>Submit</button>
                    {/*<label className='message'>su12c</label>*/}
                </form>
            </div>
        </div>
    )
}

export default RegistrationForm;
