const initialState = {
    isAuthorized: false,
    autor: false,
    like: false,
    user: null
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOGIN':
            return {
                ...state,
                isAuthorized: true,
            }
        case 'LOGOUT':
            return {
                ...state,
                isAuthorized: false
            }
        case 'AUTOR':
            return {
                ...state,
                autor: true
            };
        case 'LIKE':
            return {
                ...state,
                like: true
            };
        default:
            return state
    }
}


export default reducer;
