import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import WithServer from '../hoc';

import './loginForm.css';


function loginUser(email, password) {
    try {
        const _response = axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", {
            email,
            password,
        });
        console.log('üëâ Returned data:', _response);
        return _response
    } catch (e) {
        console.log(`üò± Axios request failed: ${e}`);
    }

}


const LoginForm = ({ Server }) => {

    //const dispatch = useDispatch()
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const state = useSelector(state => state);

    let history = useHistory();

    const nav = (link) => {
        history.push(link)
    }


    const customLogin = () => {
        console.log(Server.loginUser)
        Server.loginUser(email, password)
            .then(res => {
                if (res.statusText === "OK") {
                    nav("/list")
                }
                Server.bindUser(res.data.token)
                    .then(resp => state.user.push(resp.data))
            })
        console.log(state)
    }

    return (
        <div className="post-list">
            <div className="modal-authorization">
                <form onSubmit={(e) => e.preventDefault()}>
                    <div className="form-group">
                        <label>Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            title='Enter your email'
                            value={email}
                            onChange={(e) => setEmail(e.target.value)} />
                        <small className="form-text text-muted"></small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            value={password}
                            className="form-control"
                            placeholder="Password"
                            title='Enter your password'
                            onChange={(e) => setPassword(e.target.value)} />
                        <div className="auth-group">
                            <button className="forgotPass authHover" onClick={(e) => e.preventDefault()}>Forgot password</button>
                            <button className="signUp authHover" onClick={() => nav("/signUp")}>sign up</button>
                        </div>

                        <small className="form-text text-muted"></small>
                    </div>
                    <button type="submit" className="btn btn-outline-primary cancel" onClick={() => nav("/")}>–°ancel</button>
                    {/*<button type="submit" className="btn btn-primary confirm" onClick={(e) => {
                        e.preventDefault();
                        Server.loginUser(signInUsers)
                            .then(resp => {
                                if (resp.statusText === "OK") {
                                    nav("/list")
                                }
                                console.log(resp.data.token)
                                Server.bindUser(resp.data.token)
                                    .then(res => {
                                        console.log(res.data)
                                        state.user.push(res.data)
                                        console.log(state)

                                    })
                            })

                    }}>Sign in</button>*/}
                    <button onClick={() => customLogin()} >go</button>
                </form>
            </div>
        </div >
    )
}


export default WithServer()(LoginForm);
