import axios from 'axios';

export default class Server {

    async loginUser(email, password) {
        try {
            const res = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/auth", {
                email,
                password,
            });
            console.log('üëâ Returned data:', res);
            return res
        } catch (e) {
            console.log(`üò± Axios request failed: ${e}`);
        }

    }

    async authorizationUser(token) {
        try {
            const _res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/auth/user', { headers: { "Authorization": `Bearer ${token}` } })
            return _res
        } catch (e) {
            console.log(e)
        }
    }

    async createNewUser(email, password, name) {
        try {
            const res = await axios.post("https://nodejs-test-api-blog.herokuapp.com/api/v1/users", {
                email,
                password,
                name
            });
            console.log('üëâ Returned data:', res);
            return res
        } catch (e) {
            console.log(e.response);
            console.log(`üò± Axios request failed: ${e}`);

        }
    }

    async getAllUsers() {
        try {
            const res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/users')
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response);
            console.log(`üò± Axios request failed: ${e}`);
        }
    }

    async getAllPosts() {
        try {
            const res = await axios.get('https://nodejs-test-api-blog.herokuapp.com/api/v1/posts')
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response)
            console.log(`üò± Axios request failed: ${e}`);
        }
    }

    async getPost(id) {
        try {
            const res = await axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/posts/${id}`)
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response)
            console.log(`üò± Axios request failed: ${e}`);
        }
    }


    async getUser(id) {
        try {
            const res = await axios.get(`https://nodejs-test-api-blog.herokuapp.com/api/v1/users/${id}`)
            console.log(res)
            return res
        } catch (e) {
            console.log(e.response)
            console.log(`üò± Axios request failed: ${e}`);
        }
    }
}
