import React, { useEffect, useRef, useState } from 'react';
import WithServer from '../hoc';
import { useHistory, useParams } from 'react-router';
import { useDispatch, useSelector } from 'react-redux';
import { loaded, remove, fault } from '../../actions';
import ModalConfirm from '../modalConfirm';
import Spinner from '../spinner'
import Error from '../error';

import defaultImage from './default-image.jpg'


const CreateEditPost = ({ Server }) => {
    //useSelector
    const error = useSelector(state => state.error)
    const loading = useSelector(state => state.loading)

    //refs
    const refImagePost = useRef()
    const refMessage = useRef()

    // Modal 
    const [show, setShow] = useState(false)
    const closeDeleteModal = () => setShow(false)
    const openDeleteModal = () => setShow(true)


    //useState
    const [post, setPost] = useState(null)
    //inp
    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [fullText, setFullText] = useState('')
    const [message, setMessage] = useState('')

    const [image, setImage] = useState()

    //useDispatch
    const dispatch = useDispatch()

    //History
    const history = useHistory()

    //useParams
    const { id } = useParams()

    // token 
    const token = localStorage.getItem('token')
    const user = JSON.parse(localStorage.getItem('user'));

    //Effect and func
    //edit
    const messageResponse = (ref, message, color) => {
        ref.current.classList.add(color)
        setMessage(message)
        setTimeout(() => setMessage(''), 1500)
    }

    const changePost = (id, token, title, description, fullText) => {
        Server.updatePost(id, token, title, description, fullText)
            .then(res => {
                console.log(res)
                messageResponse(refMessage, 'Sucсess', 'green')
                setTimeout(() => history.push(`/user-posts/${user._id}`), 1500)
            })
            .catch(e => messageResponse(refMessage, 'Something went wrong', 'red'))
    }

    const testImage = (e) => {
        let formData = new FormData()
        formData.append('image', e.target.files[0])
        setImage(formData)
        console.log(image)
    }

    const changePostImage = (e) => {
        let formData = new FormData()
        formData.append('image', e.target.files[0])
        Server.updatePostImage(id, token, formData)
            .then(res => {
                messageResponse(refMessage, 'Sucсess', 'green')
                Server.getPost(id)
                    .then(res => Server.getImage(refImagePost, res.data.image))
            })
            .catch(e => messageResponse(refMessage, 'Something went wrong', 'red'))
    }

    const delPost = () => {
        Server.deletePost(id, token)
            .then(res => {
                closeDeleteModal()
                dispatch(remove(true))
                setTimeout(() => {
                    history.push(`/list/${0}`)
                    dispatch(remove(false))
                }, 500)
            })
    }
    //create 
    const createPost = () => {
        Server.createNewPost(title, fullText, description, token)
            .then(res => {
                if (res.status >= 400) {
                    setMessage(res.data.error[0].message)
                } if (res.status === 200) {
                    setTimeout(() => history.push(`/user-posts/${user._id}`), 1500)
                }
            })
            .catch(e => dispatch(fault()))
    }

    useEffect(() => {
        if (id) {
            Server.getPost(id)
                .then(res => {
                    dispatch(loaded())
                    setPost(res.data)
                    Server.getImage(refImagePost, res.data.image, defaultImage, '')
                })
                .catch(res => setPost(null))
        }
    }, [Server, dispatch, id])


    const form =
        <div className='editor'>
            <ModalConfirm
                show={show}
                handleClose={closeDeleteModal}
                remove={delPost}
                label={'Are you sure you want to delete this post?'}
            />
            <div className='editor-body'>

                <div className="item">
                    <div className="item-post">
                        <div className="post-item-list">
                            {id && <button
                                className='menu-post delete'
                                onClick={() => openDeleteModal()}
                            ><i className="fa fa-trash-o"></i></button>}
                            <div className="info-post">
                                <div className='editor-header-post'>
                                    <input type="text"
                                        title='Title your post'
                                        className="title editor-title"
                                        placeholder={post ? post.title : 'Title your post'}
                                        value={title}
                                        onChange={(e) => setTitle(e.target.value)}
                                    />
                                </div>
                                <div className='editor-description'>
                                    <input
                                        title='Description your post'
                                        type="text"
                                        className=" editor-description"
                                        placeholder={post ? post.description : 'Description your post'}
                                        value={description}
                                        onChange={(e) => setDescription(e.target.value)}
                                    />
                                </div>
                                <div className='editor-image'>
                                    <img src={defaultImage} alt='post_image' className="post-image" ref={refImagePost} />
                                    <label htmlFor='file-upload' className='inp-upload' >
                                        <i className="fa fa-camera"></i>
                                        <input
                                            type='file'
                                            accept="image/*"
                                            id="file-upload"
                                            //onChange={(e) => changePostImage(e)}
                                            onChange={(e) => testImage(e)}
                                        />
                                    </label>
                                </div>
                                <div className='editor-full-text'>
                                    <textarea
                                        title='Full text your post'
                                        className="form-control editor-full-text"
                                        rows="2"
                                        placeholder={post ? post.fullText : 'Full text your post'}
                                        value={fullText}
                                        onChange={(e) => setFullText(e.target.value)}
                                    >
                                    </textarea>

                                </div>
                                <p className='message' ref={refMessage}>{message}</p>
                                {id
                                    ? <button className='save' onClick={() => changePost(id, token, title, description, fullText)}>Edit</button>
                                    : <button className='btn-create' onClick={createPost}>Create post</button>
                                }
                            </div>
                        </div>
                    </div>
                </div>

            </div>
        </div>

    //if (loading) {
    //    return <Spinner />
    //}

    //if (error) {
    //    return <Error />
    //}

    return (
        <>
            {form}
        </>
    )
}

export default WithServer()(CreateEditPost);
