const addUser = (user) => {
    return {
        type: 'NEW_USER',
        payload: user
    }

}

const logout = () => {
    return {
        type: 'LOGOUT'
    }
}

const addToken = (token) => {
    return {
        type: 'ADD_TOKEN',
        payload: token
    }
}

const removeToken = () => {
    return {
        type: 'REMOVE_TOKEN'
    }
}

const someErorr = (bool) => {
    return {
        type: 'ERROR',
        payload: bool
    }
}

const allUsers = (allUsers) => {
    return {
        type: 'ALL_USERS',
        payload: allUsers
    }
}

export {
    addUser,
    logout,
    addToken,
    removeToken,
    someErorr,
    allUsers
}
