import { LOGIN_USER } from './types'

const initialState = {
    isAuthorized: false,
}

function rootReducer(state = initialState, action) {
    switch (action.type) {
        case 'LOGIN_USER': {
            debugger;
            return {
                ...state,
                isAuthorized: true,
            }
        }

        case 'CREATE_POST': {
            return {
                ...state,
            }
        }


        default: return state;
    }

}

export default rootReducer;
