import { autor } from "../actions";

const initialState = {
    autor: true,
    list: false
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'AUTOR':
            return {
                ...state,
                autor: !autor
            };
        case 'LIST':
            return {
                ...state,
                list: true
            };
        default:
            return state
    }
}

export default reducer;
