import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import WithServer from '../hoc';


import './registrationForm.css';

const RegistrationForm = ({ Server }) => {
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    let history = useHistory();

    const nav = (link) => {
        history.push(link)
    }

    const registerUser = () => {
        Server.createNewUser(email, password, name)
            .then(resp => {
                if (resp.statusText === "OK") {
                    nav("/login")
                }
            })
            .catch(error => {
                console.log(error)
            })
    }

    return (
        <div className="post-list">
            <div className="modal-registration">
                <form onSubmit={(e) => e.preventDefault()}>
                    <div className="form-group">
                        <label>Name</label>
                        <input
                            type="name"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter name"
                            value={name}
                            onChange={(e) => setName(e.target.value)} />
                        <small className="form-text text-muted text-light">What is your name ?</small>
                    </div>
                    <div className="form-group">
                        <label >Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            aria-describedby="emailHelp"
                            placeholder="Enter email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)} />
                        <small className="form-text text-muted">We'll never share your email with anyone else.</small>
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)} />
                        <small className="form-text text-muted">Password must be at least 5 characters. <br /> We will
                 never share your password with anyone.</small>
                    </div>
                    <button type="submit" className="btn btn-primary" onClick={registerUser}>Submit</button>
                    {/*<label className='message'>su12c</label>*/}
                </form>
            </div>
        </div>
    )
}

export default WithServer()(RegistrationForm);
