import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useHistory } from 'react-router-dom';
//import { login } from '../../actions';

import './app-header.css';

const AppHeader = () => {
    //const isAuthorized = useSelector(state => state.isAuthorized)
    const user = useSelector(state => state.user)
    //const dispatch = useDispatch()

    //const [isAuthorized, setIsAuthorized] = useState()

    let history = useHistory();
    const nav = (link) => {
        history.push(link);
    }

    const logOut = () => {
        localStorage.removeItem('token')
        nav("/login")
    }

    //const isAuthorized = localStorage.getItem('token')

    //useEffect(() => {
    //    localStorage.getItem('token')
    //}, [isAuthorized])

    //useEffect(() => {
    //    function checkUserData() {
    //        const item = localStorage.getItem('token')

    //        if (item) {
    //            setIsAuthorized(item)
    //        }
    //    }

    //    window.addEventListener('storage', checkUserData)

    //    return () => {
    //        window.removeEventListener('storage', checkUserData)
    //    }
    //}, [])

    return (
        <header className="header">
            <Link to={'/'} className="h3 text-light">PIPITER</Link>
            <nav className="navbar navbar-light">
                <form className="form-inline" onSubmit={(e) => e.preventDefault()}>
                    <input className="form-control mr-sm-2 " type="search" placeholder="Search" aria-label="Search" />
                    <button className="btn btn-outline-secondary my-2 my-sm-0" type="submit" onClick={() => nav("/list")}>Search</button>
                </form>
            </nav>
            {!user ?
                <button className="log btn btn-dark" onClick={() => {
                    //dispatch(login())
                    nav("/login")
                }}>Sign in</button> :
                <button className="log btn btn-dark" onClick={logOut}>Log out</button>
            }

        </header>
    )
}



export default AppHeader;
