import { POSTS } from './types'

const initialState = {
    postOnPage: {},
    error: false,
}

function postsReducer(state = initialState, action) {
    switch (action.type) {
        case POSTS:
            if (state.postOnPage === []) {
                return {
                    ...state,
                }
            }
            return {
                ...state,
                postOnPage: action.payload,
            }
        default: return state;
    }

}

export default postsReducer;
