//import axios from 'axios';
import { POSTS, LOADED, ERROR, DELETED } from './types'

const posts = (posts) => {
    return (dispatch) =>
        dispatch({ type: POSTS, payload: posts })
}

const loaded = () => {
    return (dispatch) =>
        dispatch({ type: LOADED })
}

const fault = () => {
    return (dispatch) =>
        dispatch({ type: ERROR })
}

const deletePost = (action) => {
    return (dispatch) =>
        dispatch({ type: DELETED, payload: action })
}

export { posts, loaded, fault, deletePost }
