import React, { useEffect, useRef, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
import ModalAuthorized from './modalAuthorized'
import { useHistory, useParams } from 'react-router';
import Spinner from '../spinner';
import Error from '../error';
//import { loaded, fault } from '../../actions';
import { loaded, fault, getPost } from '../../redux/posts/actions'

import prof from './no-avatar.jpg'
import deletProf from './delete-user.jpg'
import defaultImage from './default-image.jpg'

import './post-list.sass';

const PostList = ({ Server }) => {
    const defaultAvatar = 'https://bizraise.pro/wp-content/uploads/2014/09/no-avatar-300x300.png'

    //Hook

    // useState
    const [post, setPost] = useState({});
    const [postUser, setPostUser] = useState({});
    const [date, setDate] = useState('');
    const [heart, setHeart] = useState('');
    const [modal, setModal] = useState('')
    const [showLike, setShowLike] = useState();

    //useDispatch & useSelector
    const dispatch = useDispatch()
    //const loading = useSelector(state => state.loading)
    //const error = useSelector(state => state.error)
    //new
    const loading = useSelector(state => state.postState.loading)
    const error = useSelector(state => state.postState.error)
    const postInState = useSelector(state => state.postState.post)

    // useHistory & useParams
    const history = useHistory()
    const { id } = useParams()

    //Ref
    const refHeart = useRef()
    const refAvatar = useRef()
    const refImagePost = useRef()

    //local
    const isAuthorized = localStorage.getItem('token');
    const isAuthorizedUser = JSON.parse(localStorage.getItem('user'))

    //DateCreated
    const dateСorrector = (postData) => {
        let date = postData
        let newDate = date.replace(/[T-Z]/g, ' ')
        setDate(newDate.replace(/\..*/, ''))
    }

    // If an authorized user has a like on a post

    function saveLike(likedUser, e) {
        const idxLike = likedUser.indexOf(isAuthorizedUser._id)
        if (idxLike >= 0) {
            setShowLike(true)
        } else {
            setShowLike(false)
        }
    }

    // Func get post and user
    //useEffect(() => {
    //    Server.getPost(id)
    //        .then(res => {
    //            dispatch(loaded())
    //            setPost(res.data)
    //            setHeart(res.data.likes.length)
    //            if (isAuthorized) {
    //                saveLike(res.data.likes, refHeart.current)
    //            }
    //            dateСorrector(res.data.dateCreated)
    //            Server.getImage(refImagePost, res.data.image, defaultImage, '')
    //            Server.getUser(res.data.postedBy)
    //                .then(res => {
    //                    Server.getImage(refAvatar, res.data.avatar, defaultAvatar)
    //                    setPostUser(res.data)
    //                })
    //        })
    //        .catch(() => dispatch(fault()))
    //    //eslint-disable-next-line react-hooks/exhaustive-deps
    //}, [Server, id])

    useEffect(() => {
        if (!postInState) {
            dispatch(getPost(id))
        }
        if (postInState) {
            setHeart(postInState.likes.length)
            if (isAuthorized) {
                saveLike(postInState.likes, refHeart.current)
            }
            dateСorrector(postInState.dateCreated)
            Server.getImage(refImagePost, postInState.image, defaultImage, '')
            Server.getUser(postInState.postedBy)
                .then(res => {
                    Server.getImage(refAvatar, res.data.avatar, defaultAvatar)
                    setPostUser(res.data)
                })
        }
        //eslint-disable-next-line react-hooks/exhaustive-deps
    }, [Server, dispatch, id, isAuthorized, postInState])

    // number of likes on the post
    const postLike = async (e) => {
        await Server.getPost(post._id)
            .then(res => {
                if (res.data.likes.length !== heart) {
                    setHeart(res.data.likes.length)
                    saveLike(res.data.likes, e)
                }
            })
    }

    // func setlike 
    async function setLike(id, token, e) {
        await Server.setLikePost(id, token)
        postLike(e)
    }

    if (loading || !post) {
        return <Spinner />
    }

    if (error) {
        return <Error />
    }



    return (
        <div className="item" onClick={() => console.log(postInState)}>
            <div className="item-post">
                <div className="post-item-list">
                    {!postUser.error
                        ?
                        <div className="info-user">
                            <img src={prof} className="post-user-avatar cntr" alt="prof" ref={refAvatar} />
                            <p> {postUser.name}</p>
                        </div>
                        :
                        <div className="info-user">
                            <img src={deletProf} className="post-user-avatar cntr" alt="prof" />
                            <p className="delete-user"> User has been deleted</p>
                        </div>}
                    <div className="info-post">
                        <div className='header-post'>
                            <p className="title">{post.title}</p>

                            {isAuthorized && post.postedBy === isAuthorizedUser._id &&
                                //<button className='btn-update-post ' onClick={() => history.push(`/editor-posts/${id}`)}>
                                //    Edit post
                                //</button>}
                                <button className='btn-update-post ' onClick={() => history.push(`/post-page/${id}`)}>
                                    Edit post
                            </button>}

                        </div>
                        <img src={defaultImage} alt={post.description} className="post-image" ref={refImagePost} />
                        <p>{post.description}</p>
                        <ul className="full-text">{post.fullText}</ul>
                        <div className="footer-post">
                            <span className="date">DATE CREATE : <br />{date}</span>
                            <div className="interaction">
                                <button
                                    className='like'
                                    onClick={() => {
                                        isAuthorized
                                            ? setLike(post._id, isAuthorized, refHeart.current)
                                            : setModal('show')
                                    }
                                    }><i ref={refHeart} className={`fa fa-heart ${showLike ? 'liked' : 'offLiked'}`}></i></button>{heart}
                            </div>
                        </div>
                    </div>

                </div>

            </div>
            <ModalAuthorized cl={modal} setModal={setModal} />
        </div>
    )
}


export default WithServer()(PostList);
