import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
import { useHistory } from 'react-router-dom';
import { posts, getAllUsers, loaded } from '../../actions';
import Spinner from '../spinner';
import SimpleSnackbar from '../snackbar';

import './post-list-item.css';


const PostListItem = ({ Server }) => {

    const allPosts = useSelector(state => state.allPosts)
    const loading = useSelector(state => state.loading)
    const isSnackbar = useSelector(state => state.isSnackbar)
    const dispatch = useDispatch()

    let history = useHistory();

    useEffect(() => {
        Server.getAllPosts()
            .then(res => {
                dispatch(posts(res.data))
                dispatch(loaded())
            })
            .catch(er => console.log(er))

        Server.getAllUsers()
            .then(res => {
                dispatch(getAllUsers(res.data))
                dispatch(loaded())
            })
            .catch(er => console.log(er))
    }, [Server, dispatch])

    const goToUserPost = async (idPost) => {
        history.push(`/post/${idPost}`)
    }

    let idx = 0;

    const postList = allPosts.map((postItem, i) => {
        idx++

        return (
            <div className="post" key={idx}>
                <div className="post-list-item" >
                    <div className="cards">
                        <p className="title">{postItem.title}</p>
                        <ul className="full-text">{postItem.description}</ul>
                        <div className="full-post">
                            <p className='text'>View full post</p>
                            <button
                                className='delete'
                                onClick={() => goToUserPost(postItem._id)}>
                                <i className="fa fa-arrow-circle-right fa-2x"></i>
                            </button>
                        </div>
                    </div>
                </div>
            </div>

        )
    })


    return (
        <>
            <div className="post-list">
                {
                    !loading
                        ? postList
                        : <Spinner />
                }
            </div>
            <SimpleSnackbar isSnackbar={isSnackbar} color={"success"} message={'Deleted successful!'} />
        </>
    )
}


export default WithServer()(PostListItem);
