const initialState = {
    error: false,
    loading: false,
    user: {},
    allUsers: [],
    allPosts: [],
    post: null,
    postUser: null
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'NEW_USER':
            return {
                ...state,
                user: action.payload
            }
        case 'LOGOUT':
            return {
                ...state,
                login: false,
                user: {}
            }
        case 'ERROR':
            return {
                ...state,
                error: action.payload
            }
        case 'ALL_USERS':
            if (state.allUsers === []) {
                return {
                    ...state,
                    loading: true
                }
            }
            return {
                ...state,
                allUsers: action.payload,
                loading: false
            }
        case 'ALL_POSTS':
            if (state.allPosts === []) {
                return {
                    ...state,
                    loading: true
                }
            }
            return {
                ...state,
                allPosts: action.payload,
                loading: false
            }
        case 'POST':
            return {
                ...state,
                post: action.payload
            }
        case 'POST_USER':
            return {
                ...state,
                postUser: action.payload
            }
        default:
            return state
    }
}


export default reducer;
