import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import WithServer from '../hoc';
import { useHistory, useParams } from 'react-router-dom';
import { posts, getAllUsers, loaded } from '../../actions';
import Spinner from '../spinner';
import SimpleSnackbar from '../snackbar';

import './post-list-item.css';


const PostListItem = ({ Server }) => {

    const allPosts = useSelector(state => state.allPosts)
    const loading = useSelector(state => state.loading)
    const isSnackbar = useSelector(state => state.isSnackbar)
    const dispatch = useDispatch()

    const { skip } = useParams();

    let history = useHistory();

    //const getPosts = (skipped) => {
    //    Server.getAllPosts(skipped)
    //        .then(res => {
    //            dispatch(posts(res.data))
    //            dispatch(loaded())
    //        })
    //        .catch(er => console.log(er))
    //}
    useEffect(() => {
        Server.getAllPosts(skip)
            .then(res => {
                dispatch(posts(res.data))
                dispatch(loaded())
            })
            .catch(er => console.log(er))
        //getPosts(0)

        //Server.getAllUsers()
        //    .then(res => {
        //        dispatch(getAllUsers(res.data))
        //        dispatch(loaded())
        //    })
        //    .catch(er => console.log(er))
    }, [Server, dispatch, skip])

    const goToUserPost = async (idPost) => {
        history.push(`/post/${idPost}`)
    }

    let idx = 0;

    const postList = allPosts.map((postItem, i) => {
        idx++

        return (
            <div className="post" key={idx}>
                <div className="post-list-item" >
                    <div className="cards">
                        <p className="title">{postItem.title}</p>
                        <ul className="full-text">{postItem.description}</ul>
                        <div className="full-post">
                            <p className='text'>View full post</p>
                            <button
                                className='delete'
                                onClick={() => goToUserPost(postItem._id)}>
                                <i className="fa fa-arrow-circle-right fa-2x"></i>
                            </button>
                        </div>
                    </div>
                </div>
            </div>

        )
    })


    return (
        <>
            <div className='nav-posts'>
                <button className='btn-nav-posts' onClick={() => history.push(`/list/${0}`)}>1</button>
                <button className='btn-nav-posts' onClick={() => history.push(`/list/${5}`)}>2</button>
                <button className='btn-nav-posts' onClick={() => history.push(`/list/${10}`)}>3</button>
                <button className='btn-nav-posts' onClick={() => history.push(`/list/${15}`)}>4</button>
            </div>
            <div className="post-list">
                {
                    !loading
                        ? postList
                        : <Spinner />
                }
            </div>
            {/*<div className='nav-posts'>
                <button className='btn-nav-posts' onClick={() => getPosts(0)}>1</button>
                <button className='btn-nav-posts' onClick={() => getPosts(5)}>2</button>
                <button className='btn-nav-posts' onClick={() => getPosts(10)}>3</button>
                <button className='btn-nav-posts' onClick={() => getPosts(15)}>4</button>
            </div>*/}
            <SimpleSnackbar isSnackbar={isSnackbar} color={"success"} message={'Deleted successful!'} />
        </>
    )
}


export default WithServer()(PostListItem);
